"use strict";(self.webpackChunk=self.webpackChunk||[]).push([[5698],{5698:e=>{e.exports=JSON.parse('{"en":{"main_title":"Caspar David Friedrich","name":"Caspar David Friedrich","born_year":"1774","died_year":"1840","description":"Caspar David Friedrich was a landscape painter of the nineteenth-century German Romantic movement, of which he is now considered the most important painter. A painter and draughtsman, Friedrich is best known for his later allegorical landscapes, which feature contemplative figures silhouetted against night skies, morning mists, barren trees, and Gothic ruins."},"ua":{"main_title":"Каспар Давид Фрідріх","name":"Каспар Давид Фрідріх","born_year":"1774","died_year":"1840","description":"Каспар Давид Фрідріх був художником-пейзажистом німецького романтизму XIX століття, серед якого він нині вважається найважливішим художником. Художник і кресляр, Фрідріх найбільш відомий своїми пізнішими алегоричними пейзажами, на яких зображені споглядальні фігури на тлі нічного неба, ранкових туманів, безплідних дерев і готичних руїн."},"es":{"main_title":"Каспар Давид Фридрих","name":"Каспар Давид Фридрих","born_year":"1774","died_year":"1840","description":"Каспар Давид Фридрих был художником-пейзажистом немецкого романтического движения девятнадцатого века, из которых он теперь считается самым важным художником. Художник и рисовальщик, Фридрих наиболее известен своими более поздними аллегорическими пейзажами, на которых изображены созерцательные фигуры, вырисовывающиеся на фоне ночного неба, утреннего тумана, бесплодных деревьев и готических руин."}}')}}]);